import { BasicColumn } from '/@/components/Table';
import { FormSchema } from '/@/components/Table';
import { uploadImage } from '/@/api/demo/Upload';
import { h } from 'vue';
import { Image } from 'ant-design-vue';

export const columns: BasicColumn[] = [
  {
    title: '模块',
    dataIndex: 'module_id',
    width: 120,
  },
  {
    title: '标题',
    dataIndex: 'title',
    width: 120,
  },
  {
    title: '图片',
    dataIndex: 'image',
    width: 120,
    customRender: ({ value }) => {
      let images = value;

      if (typeof value === 'string') images = value.replace(/^\{(.*)\}$/, '$1').split(',');

      const result: any[] = [];

      images.forEach((image: string) => {
        result.push(
          h(Image, {
            src: image,
            height: '50px',
            width: '50px',
            fallback:
              'data:image/png;base64,',
          }),
        );
      });

      return h(Image.PreviewGroup, {}, () => result);
    },
  },
  {
    title: '链接',
    dataIndex: 'link',
    width: 120,
  },
  {
    title: '顺序',
    dataIndex: 'sub_order',
    width: 120,
    edit: true,
  },
  {
    title: '新增时间',
    dataIndex: 'created_at',
    width: 120,
  },
  {
    title: '更新时间',
    dataIndex: 'updated_at',
    width: 120,
  },
];

